const LOGIN_SUCCESS = 'LOGIN_SUCCESS';
const LOGIN_FAILIR = 'LOGIN_FAILIR';

const initialValue = {
  loading : false,
  data:[],
  error:''
}

const asynReducer = (state=initialValue,action) => {
  switch(action.type){
   case LOGIN_START:
     return {
      ...state,
      loading:true,
     }
   break;  
   case LOGIN_SUCCESS:
    return {
      loading:false,
